/*!50003 CREATE*/ /*!50017 DEFINER=`cloudxuser`@`%`*/ /*!50003 TRIGGER `Invoice_AFTER_UPDATE` AFTER UPDATE ON `Invoice` FOR EACH ROW BEGIN
