-- noformat
	pStartDate 			datetime,
	pEndDate 			datetime
	) RETURNS int
BEGIN

	set @Days = ifnull(datediff(ifnull(pEndDate, now()), pStartDate), 0);
	if @Days > 0 then 
		if time_to_sec(pEndDate) < TIME_TO_SEC(pStartDate) then 
			set @Days = @Days - 1;
		end if;
	end if;

	return @Days;

END ;;
