CREATE DEFINER=`cloudxuser`@`%` PROCEDURE `ApproverPerformance`(startDate datetime, endDate datetime)
BEGIN
  DROP TABLE IF EXISTS T1;
  DROP TABLE IF EXISTS T2;
  DROP TABLE IF EXISTS T3;
  DROP TABLE IF EXISTS T4;
  DROP TABLE IF EXISTS T5;
  DROP TABLE IF EXISTS T6;
  DROP TABLE IF EXISTS T7;
  DROP TABLE IF EXISTS T8;

  CREATE TEMPORARY TABLE T1 (
    InvoiceId int NULL,
    `Actor` nvarchar(250) NULL,
    ActionDate datetime NULL,
    PreviousData nvarchar(500) NULL,
    NewData nvarchar(500) NULL,
    Field nvarchar(250) NULL,
    InitialStatus nvarchar(50) NULL,
    CurrentStatus nvarchar(50) NULL,
    ActorId char(36) NULL
  );

  CREATE TEMPORARY TABLE T2 (
    InvoiceId int NULL,
    Actor nvarchar(250) NULL,
    ActionDate datetime NULL,
    PreviousData nvarchar(500) NULL,
    NewData nvarchar(500) NULL,
    Field nvarchar(250) NULL,
    InitialStatus nvarchar(50) NULL,
    CurrentStatus nvarchar(50) NULL,
    ActorId char(36) NULL
  );

  CREATE TEMPORARY TABLE T5 (
    InvoiceId int NULL,
    `Actor` nvarchar(250) NULL,
    ActionDate datetime NULL,
    PreviousData nvarchar(500) NULL,
    NewData nvarchar(500) NULL,
    Field nvarchar(250) NULL,
    InitialStatus nvarchar(50) NULL,
    CurrentStatus nvarchar(50) NULL,
    ActorId char(36) NULL
  );

  CREATE TEMPORARY TABLE T6 (
    InvoiceId int NULL,
    Actor nvarchar(250) NULL,
    ActionDate datetime NULL,
    PreviousData nvarchar(500) NULL,
    NewData nvarchar(500) NULL,
    Field nvarchar(250) NULL,
    InitialStatus nvarchar(50) NULL,
    CurrentStatus nvarchar(50) NULL,
    ActorId char(36) NULL
  );

  INSERT INTO T1
    SELECT
      invoiceid,
      actor,
      actiondate,
      previousdata,
      newdata,
      field,
      initialstatus,
      currentstatus,
      actorid
    FROM InvoiceHistory
    WHERE actorid IN (SELECT
        userid
      FROM `User`
      WHERE clientid = 1)
    AND initialstatus = 'captured'
    AND currentstatus = 'in approval'
    AND actiondate >= startDate
    AND actiondate < endDate;

  -- select * from T1;

  INSERT INTO T2
    SELECT
      invoiceid,
      actor,
      actiondate,
      previousdata,
      newdata,
      field,
      initialstatus,
      currentstatus,
      actorid
    FROM InvoiceHistory
    WHERE actorid IN (SELECT
        userid
      FROM `User`
      WHERE clientid = 1)
    AND initialstatus = 'in approval'
    AND currentstatus = 'approved'
    AND actiondate >= startDate
    AND actiondate < endDate;

  INSERT INTO T5
    SELECT
      invoiceid,
      actor,
      actiondate,
      previousdata,
      newdata,
      field,
      initialstatus,
      currentstatus,
      actorid
    FROM InvoiceHistory
    WHERE actorid IN (SELECT
        userid
      FROM `User`
      WHERE clientid = 1)
    AND initialstatus = 'captured'
    AND currentstatus = 'in approval'
    AND actiondate >= DATE_FORMAT(NOW(), '%Y-01-01')
    AND actiondate < endDate;

  -- select * from T1;'2016-01-01'

  INSERT INTO T6
    SELECT
      invoiceid,
      actor,
      actiondate,
      previousdata,
      newdata,
      field,
      initialstatus,
      currentstatus,
      actorid
    FROM InvoiceHistory
    WHERE actorid IN (SELECT
        userid
      FROM `User`
      WHERE clientid = 1)
    AND initialstatus = 'in approval'
    AND currentstatus = 'approved'
    AND actiondate >= DATE_FORMAT(NOW(), '%Y-01-01')
    AND actiondate < endDate;


  CREATE TEMPORARY TABLE T3 (
    assignedOn datetime,
    approveddate datetime,
    invoiceid int,
    actor nvarchar(250),
    datedif float
  );

  CREATE TEMPORARY TABLE T4 (
    assignedOn datetime,
    approveddate datetime,
    invoiceid int,
    actor nvarchar(250),
    datedif float
  );

  CREATE TEMPORARY TABLE T7 (
    assignedOn datetime,
    approveddate datetime,
    invoiceid int,
    actor nvarchar(250),
    datedif float
  );

  CREATE TEMPORARY TABLE T8 (
    assignedOn datetime,
    approveddate datetime,
    invoiceid int,
    actor nvarchar(250),
    datedif float
  );

  INSERT INTO T3
    SELECT
      o.actiondate AS assignedOn,
      t.actiondate AS approvedDate,
      o.invoiceid,
      t.actor,
      DATEDIFF(t.actiondate, o.actiondate)
    FROM T1 o
      LEFT JOIN T2 t
        ON o.invoiceid = t.invoiceid;

  INSERT INTO T4
    SELECT
      o.actiondate AS assignedOn,
      t.actiondate AS approvedDate,
      o.invoiceid,
      t.actor,
      DATEDIFF(t.actiondate, o.actiondate)
    FROM T1 o
      LEFT JOIN T2 t
        ON o.invoiceid = t.invoiceid;

  INSERT INTO T7
    SELECT
      o.actiondate AS assignedOn,
      t.actiondate AS approvedDate,
      o.invoiceid,
      t.actor,
      DATEDIFF(t.actiondate, o.actiondate)
    FROM T5 o
      LEFT JOIN T6 t
        ON o.invoiceid = t.invoiceid;

  INSERT INTO T8
    SELECT
      o.actiondate AS assignedOn,
      t.actiondate AS approvedDate,
      o.invoiceid,
      t.actor,
      DATEDIFF(t.actiondate, o.actiondate)
    FROM T5 o
      LEFT JOIN T6 t
        ON o.invoiceid = t.invoiceid;

  -- select * from T3;

  SELECT
    T3.actor,
    ROUND(AVG(T3.datedif), 5) AS `Average`,
    MAX(T3.datedif) AS `Maximum`,
    ROUND(AVG(T7.datedif), 5) AS `YearlyAverage`,
    (SELECT
        COUNT(a.invoiceid)
      FROM T4 a
      WHERE a.actor = T3.actor) AS `Total`,
    (SELECT
        COUNT(b.invoiceid)
      FROM T8 b
      WHERE b.actor = T3.actor) AS `YearlyTotal`
  -- , avg(T13.datedif) as`YearlyAverage`
  FROM T3
    INNER JOIN T7
      ON T3.actor = T7.actor
  WHERE T3.actor IS NOT NULL
  GROUP BY T3.actor;

  DROP TABLE IF EXISTS T1;
  DROP TABLE IF EXISTS T2;
  DROP TABLE IF EXISTS T3;
  DROP TABLE IF EXISTS T4;
  DROP TABLE IF EXISTS T5;
  DROP TABLE IF EXISTS T6;
