    (`Invoice`.`InvoiceId`) AS 'InvoiceId',
    `Invoice`.`InvoiceNo`,
    `InvoiceHistory`.`PreviousData`,
    `InvoiceHistory`.`NewData`,
    `InvoiceHistory`.`Field`,
