CREATE DEFINER=`cloudxuser`@`%` PROCEDURE `TotalInvoiceCountReport`(startDate datetime, endDate datetime)
BEGIN
  SELECT
    b.`Source`,
    CASE WHEN i.IsPOInvoice = 1 THEN 'PO' ELSE 'No PO' END AS POType,
    COUNT(i.InvoiceId) AS InvoiceCount
  FROM Batch b
