	BEGIN

		IF EXISTS (SELECT
					1
				FROM SystemReleases
				WHERE ReleaseID = pReleaseID
				AND ReleaseDate IS NOT NULL) THEN
			SIGNAL SQLSTATE '42000' SET MESSAGE_TEXT = 'This entry has already been released.', MYSQL_ERRNO = 1;
