	end if;

	-- call _x_Notify(@UserId, 'Client', 'Information change', 'Settings', null, null, null);

	IF @AddressId is null then
		INSERT INTO Address (StreetName, Address1, StateId, CityName, ZipCode, Country, ContactPerson, Email, PhoneNo)
