							Address1			= pAddress1,
							StateId			= pStateId,	
							CityName			= pCityName,
							ZipCode			= pZipCode,
							Country			= pCountry,
							ContactPerson		= pContactPerson,
							Email				= pEmail,
							PhoneNo			= pPhoneNo
			WHERE AddressId = @AddressId;
	end if;
	
	UPDATE Client	SET CustomerName 		= pCustomerName,
						AddressId 			= @AddressId,
						Telephone			= @PhoneNo,
						Fax 				= null,
						Email 				= pEmail,
						ContactPerson		= pContactPerson
		where Clientid = pClientId;
		
								


END ;;
