	ELSEIF (pType = 'Auditor') THEN 	
		SELECT 	UserId 													AS TypeId,
				CONCAT(LastName, ', ', FirstName) 						AS TypeName
		FROM	User
		WHERE	ClientId = pClientId
				and IsActive = 1
				and IsCloudxUser = 0
				and Regexp_Like(concat(ifnull(LastName, ''), ' ', ifnull(FirstName, ''), ' ', ifnull(UserName, ' ')), pKeyword, 'mi')
				and exists (
								SELECT	1
