		order by 2
		limit dLimitResults;
	ELSEIF (pType = 'ehandler') THEN 	
		SELECT 	User.UserId 									AS TypeId,
				CONCAT(LastName, ', ', FirstName) 				AS TypeName
		FROM	User
				JOIN	_x_UserParameters ON User.UserId = _x_UserParameters.UserId
		WHERE	ClientId = pClientId
				and IsActive = 1
				and IsCloudxUser = 0
