		limit dLimitResults;		
	ELSEIF (pType = 'category1') THEN 
		SELECT 	Id 								AS TypeId,
				CategoryName 					AS TypeName
		FROM	Category
		WHERE	ClientId = pClientId				
				AND ifnull(CategoryType, 0) = 0
