		limit dLimitResults;	
	ELSEIF (pType = 'category2') THEN 
		SELECT 	Id 								AS TypeId,
				CategoryName 					AS TypeName
		FROM	Category
		WHERE	ClientId = pClientId				
				AND CategoryType = 1
				and Regexp_Like(CategoryName, pKeyword, 'mi')
