	if exists (SELECT	cr.*
				FROM	ClientUserRoles cur
						INNER JOIN ClientRoles cr				ON cr.RoleId = cur.RoleId
				WHERE 	cr.isAdmin	= 1
						aND cur.UserId	= pUserId
