-- 					Values (pInvoiceId, 99, 'Missing line items.');
-- 			end if;
-- 			set @AmountCheckPlus 	= @InvoiceTotal + @InvoiceTaxAmount + @FreightAmount;
-- 			set @AmountCheckMinus 	= @InvoiceTotal + @InvoiceTaxAmount + @FreightAmount;
-- 
