		Call _x_LineItemDataChanges(pClientId, pInvoiceId, -1, pUserId, 'After');
		
	ELSEIF (pType = 3) THEN -- Insert from PO
		Call _x_LineItemDataChanges(pClientId, pInvoiceId, -1, pUserId, 'Before');
