IN `pInvoiceId` int,
IN `pPercent` decimal(18, 2),
IN `pDateFrom` datetime,
IN `pDateTo` datetime)
BEGIN

  UPDATE Invoice
  SET DiscountRequestedAmount = pPercent,
      PaymentProcessingDate = pDateFrom,
      DiscountExpireDate = pDateTo
