	SELECT 	GROUP_CONCAT(concat(InternalField, if(SortOrder>0,' ASC', ' DESC')) order BY abs(SortOrder) SEPARATOR ',')
	INTO 	@SQLSelectOrderBy
	FROM 	myReportFieldOutput
