		ELSE
			SET @ErrMsg = CONCAT('Improperly coded string; Unknown keyword [', DataValue, '].');
			SIGNAL SQLSTATE '42000' SET MESSAGE_TEXT = @ErrMsg, MYSQL_ERRNO = 1;
			LEAVE ThisProc;
		END IF;

	END LOOP;
	CLOSE curParms;


