CREATE DEFINER=`cloudxuser`@`%` PROCEDURE `_x_UserDelete`(IN `pType` tinyint,
IN `pId` int,
IN `pUserId` char(36))
BEGIN

  SET FOREIGN_KEY_CHECKS = 0;

  IF (pType = 1) THEN

    DELETE
      FROM ApproverGLCode
    WHERE Id = pId;

  ELSEIF (pType = 2) THEN

    DELETE
      FROM UserDepartment
    WHERE Id = pId;

  ELSEIF (pType = 3) THEN

    DELETE
      FROM ApproverByAmount
    WHERE Id = pId;

  ELSEIF (pType = 4) THEN

    DELETE
      FROM UserReassignmentHistory
    WHERE ReassignUserHistoryId = pId;

  ELSEIF (pType = 5) THEN

    DELETE
      FROM APClerkVendor
    WHERE APClerkId = pUserId
      AND VendorId = pId;

  ELSE

    DELETE
      FROM UserClientCompanies
    WHERE UserId = pUserId
      AND ClientCompanyId = pId;

  END IF;

  SET FOREIGN_KEY_CHECKS = 1;

END ;;
