ThisProc:

-- noformat

	BEGIN
		IF pIsEnabled = 1 THEN
			UPDATE _x_UserParameters 
			SET AccessTail = '00',
				AccessDate = DATE_SUB(UTC_TIMESTAMP(), INTERVAL 1 DAY)
			WHERE UserId = pUserId;
